import HeroImg from "../assets/niyami-removebg-preview.png";
import {
  AiOutlineTwitter,
  AiOutlineFacebook,
  AiOutlineLinkedin,
  AiOutlineInstagram,
  AiOutlineGithub,
} from "react-icons/ai";

export default function Hero() {
  const config = {
    subtitle: "Im a Full-stack developer and Designer",
    social: {
      twitter: "https://twitter.com/jvlcode",
      facebook: "https://www.facebook.com/rashid.rizan.9/",
      linkedin: "https://in.linkedin.com/company/jvl-code",
    },
  };

  return (
    <section className="flex flex-col md:flex-row px-5 py-32 bg-secondary justify-center">
      <div className="md:w-1/2 flex flex-col">
        <h1 className=" text-white text-6xl ">
          Hi, <br /> I'm{" "}
          <span className="text-primary hover:text-black">Ahamed</span> Rashid
          <p className="text-2xl">{config.subtitle}</p>
        </h1>
        <div className="flex p:8 py-10">
          <a
            href={config.social.facebook}
            className="pr-15 text-white  hover:text-white"
          >
            <AiOutlineFacebook size={40} />
          </a>
          <a
            href={config.social.linkedin}
            className="text-white  hover:text-white"
          >
            <AiOutlineInstagram size={40} />
          </a>
          <a
            href={config.social.linkedin}
            className="text-white  hover:text-white"
          >
            <AiOutlineGithub size={40} />
          </a>
          <a
            href={config.social.linkedin}
            className="text-white  hover:text-white"
          >
            <AiOutlineLinkedin size={40} />
          </a>
        </div>
      </div>
      <img className="md:w-1/3" src={HeroImg} />
    </section>
  );
}
